    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int o = Integer.parseInt(txt_p.getText());
        if(o > 0 & o < 390){
            AreaLectura.setText("");       File archivo = new File("SegundoAño/HistoriaUni/" +o +".txt");    
        try { BufferedReader leer = new BufferedReader(new FileReader(archivo)); String linea = leer.readLine();
            while(linea != null){
                PanelLeer_Area.setVisible(true); AreaLectura.append(linea + "\n"); linea = leer.readLine();
            }} catch (FileNotFoundException ex) { Logger.getLogger(HistoriaUniversal.class.getName()).log(Level.SEVERE, null, ex);
        } catch (IOException ex) { Logger.getLogger(HistoriaUniversal.class.getName()).log(Level.SEVERE, null, ex); }
       }else{
           
            JOptionPane.showMessageDialog(null, "Ingrese un valor valido porfavor.");
        }
        txt_p.setText("");
    }//GEN-LAST:event_jButton1ActionPerformed

